import React,{Component} from 'react';
import Client from '../consulta';
import {Line} from 'react-chartjs-2';
var _ = require('lodash');


class BiceGraficas extends Component{
    constructor(props){
        super(props);
        const dato=this.props.cosas.match.params
         this.state={
             name:'',
             prioridad1: props.valor1,
             prioridad2: dato.id
         }
         this.handleChange = this.handleChange.bind(this);
         this.handleSubmit = this.handleSubmit.bind(this);
         this.handleInputChangeM= this.handleInputChangeM.bind(this);
     };  
     handleChange(event) {
         this.setState({value: event.target.value});
       }
     handleSubmit(event) {
         let parte=JSON.stringify(this.state.value);
         if(this.state.value){
             Client.search("{ nick:"+parte+" }", respuesta => {
                 let guarda = (JSON.parse(respuesta));
             });      
        }
       event.preventDefault();
     }    
     handleInputChangeM(e) {
         e.preventDefault();
         const {value, name} = e.target;
         const esto=' {"json": { "pide":"' +value+ '", "tipo":"1" } } '
         Client.search(esto, respuesta => {
             this.setState({
                 name: respuesta
               });
         });      
       }
 
    render(){

        let state = {
            labels: [],
            datasets: [
              {
                label: 'ESTADOS',
                fill: false,
                lineTension: 0.5,
                backgroundColor: 'rgba(175,192,192,1)',
                borderColor: 'rgba(255,2,255,1)',
                borderWidth: 2,
                data: []
              }
            ]
          }
        state.datasets[0].data.push(4)
        let yy
        let muestra = this.state.name;
        if(muestra) yy = JSON.parse(muestra)
        const que = _.map( yy, (function(item){
            return item;
         }))

        let x=1
        let dd=[]
        let ee=[]
        let es=_.forEach(que[3], function(n, key) {
            //console.log('a=',n, 'b=',key,'x=',x);
            dd.push(n)
            ee.push(x)
            x++
          });        

          state.datasets[0].data = dd
          state.labels = dd

         if(que) console.log('salida=', que[0])
         if(que) console.log('salida=', que[1])
         if(que) console.log('salida=', que[2])

         return(
             
             <div>

                        <div className="col-sm-8">
                            <div className="card">
                            <div className="card-body">
                    <Line
                            data={state}
                            options={{
                                title:{
                                display:true,
                                text:'Grafica mensual',
                                fontSize:40
                                },
                                legend:{
                                display:true,
                                position:'right'
                                }
                            }}
                            />
                    </div>
                    </div>
                    </div>


                        <div className="col-sm-8">
                            <div className="card">
                            <div className="card-body">
    
                            <select
                                name="priority"
                                className="form-control"
                                value={this.state.priority}
                                onChange={this.handleInputChangeM}
                            >
                                <option>cobre</option>
                                <option>dolar</option>
                            </select>
                            <p className="card-text"> </p>

                            <div className="card-body" className="col-sm-2">
                                     <button className="btn btn-primary">{que[0]}</button>
                                     <br></br>
                                     <button className="btn btn-primary">{que[1]}</button>
                                     <button className="btn btn-primary">{que[2]}</button>

                                    {/* <button className="btn btn-primary">{esto.name}</button>
                                    <span className="badge badge-pill badge-dander ml-12">Unidad</span>
                                    <button className="btn btn-primary">{esto.unit}</button>
                                    <span className="badge badge-pill badge-dander ml-12">fecha</span>
                                    <button className="btn btn-primary">{esto.date}</button>
                                    <span className="badge badge-pill badge-dander ml-12">valor</span>
                                    <button className="btn btn-primary">{esto.value}</button> */}
                            </div>
    
    
                            </div>
                            </div>
                        </div>




                {/* <div className="card text-white bg-primary mb-12" >
                      <form action="/" method="get" >
                          <button className="btn btn-primary"type="submit">Volver</button>
                       </form>                
                </div> */}

                
             </div>
        )
    }


}
export default BiceGraficas;
