import React,{Component} from 'react';
import Client from '../consulta';
import ClientDelete from '../Borra';
import ClientInserta from '../Inserta';
import ClientModifica from '../modifica';

class FormaInsert extends Component {
    constructor(props) {
      super(props);
      this.state = {
            extra:'',
            nick:'',
            msg:'',
            salida:'',
            value: '',
            valor:'',
            persona:{ 
                nick: '',
                msg: '',
                created: { type: '', default: '' }
                },
            };
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
      this.handleInsertar = this.handleInsertar.bind(this);
      this.handleInputChange = this.handleInputChange.bind(this);
      this.handleRemove = this.handleRemove.bind(this);

    }
  

    handleChange(event) {
      this.setState({value: event.target.value});
  }
  
    handleSubmit(event) {
        let parte=JSON.stringify(this.state.value);
        if(this.state.value){
            Client.search("{ nick:"+parte+" }", respuesta => {
                let guarda = (JSON.parse(respuesta));
                this.setState( {salida:guarda}); 
                this.refs.someName.value = '';
            });      
       }
      event.preventDefault();
    }
    handleModifica = (event) => {
        this.state.salida.map((este)=>{
             if(este._id===event.target.id){
               // console.log(este._id+':'+este.nick+':este'+este.msg);
                var object2 = this.refs.aca2;  
                //console.log('nick:'+ este.nick);
                //console.log('msg:'+ object2.value);
                this.setState(
                    { persona:{
                                nick: este.nick,
                                msg: object2.value,
                                created: { type: '13e', default: este._id }
                              }
                    },
                    ()=>{
                        const aa=JSON.stringify(this.state.persona);
                        ClientModifica.Modif(aa, respuesta => {
                            let parte=JSON.stringify(this.state.nick);
                            if(this.state.nick){
                                Client.search("{ nick:"+parte+" }", respuesta => {
                                    let guarda = (JSON.parse(respuesta));
                                    this.setState( {salida:guarda}); 
                                    this.refs.someName.value = '';
                                });      
                           }                    
                         });
                    }); 
             }
         return 'listo'    
        })
      }
    handleRemove(event) {
        const target = event.target;
        const name = target.name;
        const id = target.id;
        ClientDelete.deleteC(id, respuesta => {
            let parte=JSON.stringify(name);
            Client.search("{ nick:"+parte+" }", respuesta => {
                let guarda = (JSON.parse(respuesta));
                this.setState( {salida:guarda}); 
            });      
        });      
        event.preventDefault();
    } 

    handleInsertar(e) {
        this.setState(
            { persona:{
                        nick: this.state.nick,
                        msg: this.state.msg,
                        created: { type: '13e', default: '45234' }
                      }
            },
            ()=>{
                const aa=JSON.stringify(this.state.persona);
                ClientInserta.Inserta(aa, respuesta => {


                    let parte=JSON.stringify(this.state.nick);
                    if(this.state.nick){
                        Client.search("{ nick:"+parte+" }", respuesta => {
                            let guarda = (JSON.parse(respuesta));
                            this.setState( {salida:guarda}); 
                            this.refs.someName.value = '';
                        });      
                   }                    
                    });
            }); 

       e.preventDefault();
    }


    handleInputChange(event) {
        const target = event.target;
        const value =  target.value;
        const name = target.name;
        
        if(name==='nick'){
            this.setState( {nick: value } ); 
        }
        if(name==='msg'){
            this.setState( { msg: value } ); 
        }
        event.preventDefault();
      }


    render() {
      let options33= this.state.salida;
      
      return (

       <div className="card text-center border-info"> 

        <div className="card text-center border-info">
          <div className="card-body">
            <h4 className="card-title">Respuestas de MongoBD</h4>
               <table className="table">
                <tbody>
                    {
                        Object.keys(options33).map((keyName, i) => (
                            <tr key={i}>
                                <th>
                                Id:{options33[keyName]._id}  
                                </th>
                                <th>
                                nick={options33[keyName].nick} 
                                </th>
                                <th>
                                msg={options33[keyName].msg}
                                </th>
                                <th>
                                <button className="badge badge-danger" key={i+0}name={options33[keyName].nick} id={options33[keyName]._id} onClick={this.handleRemove}>Borra</button>
                                <button className="badge badge-primary"key={i+1}name={options33[keyName].nick} id={options33[keyName]._id} onClick={this.handleModifica}>Modifica</button>
                                </th>
                            </tr>
                        ))
                    }        
                 </tbody>
                </table>                
              </div>
        </div> 


                <div className="card text-center border-info">
                    <div className="card-body">
                    <h4 className="card-title">Insertar</h4>
                         <div className="card text-center border-info">
                           {
                                <form id='123' onSubmit={this.handleInsertar} >
                                    nick:<input ref='aca1'name="nick"type="text"onChange={this.handleInputChange} />
                                    msg:<input ref='aca2'name="msg"type="text"onChange={this.handleInputChange} />
                                    <input type="submit" value="Graba en MongoDB" />
                                </form>
                             }
                         </div>
                        
                    </div>
                </div> 

            <div className="card-body">
            <h4 className="card-title">Consulta</h4>          
                    {/* <label> {this.state.valor}</label>                 */}
                    <form id='kk'name="2" className="card-body"onSubmit={this.handleSubmit} >
                        nick:< input name="23" id='rr'type="text"  onChange={this.handleChange}  ref="someName"/>
                       <input id='33' name="22"  type="submit" value="Envia1" />
                    </form>
            </div>   

            <ul className="pagination">
            <li className="page-item"><a className="page-link" href="/">Previous</a></li>
            <li className="page-item"><a className="page-link" href="/">1</a></li>
            <li className="page-item"><a className="page-link" href="/">2</a></li>
            <li className="page-item"><a className="page-link" href="/">3</a></li>
            <li className="page-item"><a className="page-link" href="/">Next</a></li>
            </ul>

        </div>   
      );
    }
  }
export default FormaInsert;
