import React,{Component} from 'react';
import Client from '../consulta';

class Forma extends Component {
    constructor(props) {
      super(props);
      this.state = {
            salida:'',
            value: '',
            valor:'',
            persona:{ 
                nick: '',
                msg: '',
                created: { type: '', default: '' }
                },

            };
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }
  
    handleChange(event) {
      this.setState({value: event.target.value});
    }
  
    handleSubmit(event) {
        let parte=JSON.stringify(this.state.value);
        console.log('marco1',parte)
        if(this.state.value){
            console.log('marco2',parte)
            Client.search("{ nick:"+parte+" }", respuesta => {
                console.log('marco3',respuesta)
                let guarda = (JSON.parse(respuesta));
                // this.setState( {salida:guarda}); 
                // this.refs.someName.value = '';
            });      
       }
      event.preventDefault();
    }


    render() {
      //let options3= this.state.persona;
      let options3= this.state.salida;
      
      return (

        <div className="card text-center border-info">

            <nav className="navbar navbar-expand-sm bg-dark navbar-dark">
                    <ul className="navbar-nav">
                        <li className="nav-item">
                        <a className="nav-link" href="/">menu1</a>
                        </li>
                        <li className="nav-item">
                        <a className="nav-link" href="/">menu2</a>
                        </li>
                    </ul>
            </nav>


          <div className="form-group">
            <select
                name="priority"
                className="form-control"
                value={this.state.priority}
                onChange={this.handleInputChange}
              >
              <option>low</option>
              <option>medium</option>
              <option>high</option>
            </select>
          </div>

                <div className="card text-center border-info">
                    <div className="card-body">
                    <h4 className="card-title">Respuesta MongoBD</h4>
                        <ul>{
                            Object.keys(options3).map((keyName, i) => (
                                <li key={i}>
                                    Id:{options3[keyName]._id}  nick={options3[keyName].nick} msg={options3[keyName].msg}
                                </li>
                            ))
                        }
                        </ul>
                    </div>
                </div> 

            <div className="card-body">
            <h4 className="card-title">Haga Consultas aqui</h4>          
                    {/* <label> {this.state.valor}</label>                 */}
                    <form className="card-body"onSubmit={this.handleSubmit} >
                        nick1:<input type="text"  onChange={this.handleChange}  ref="someName"/>
                    <input type="submit" value="ConsultaAqui" />
                    </form>

                    <form action="/pagina5/1" method="get" >
                    <button type="submit">Acciones</button>
                    </form>

            </div>   
            
        </div>   

      );
    }
  }
export default Forma;
