import React,{Component} from 'react';
import Client from '../consulta';
import {Line} from 'react-chartjs-2';
var _ = require('lodash');


class BiceGraficas extends Component{
    constructor(props){
        super(props);
         this.state={
             name:'',
             data:{},
             grafica:{},
             info:[],
             valores:{},
             combo:[{name:1},{name:2},{name:3}]
         }
         this.handleInputChange= this.handleInputChange.bind(this);
     };  

     getData(){
        Client.search( respuesta => {
            this.setState({
                info: respuesta.apis
              });
             const thisOne = respuesta.apis[1].example.response

             this.setState({
                data: thisOne
              });

             let state = {
                labels: [],
                datasets: [
                  {
                    label: 'BICE ESTADOS',
                    fill: false,
                    lineTension: 0.5,
                    backgroundColor: 'rgba(175,192,192,1)',
                    borderColor: 'rgba(255,2,255,1)',
                    borderWidth: 2,
                    data: []
                  }
                ]
              }
              this.setState({
                grafica: state
              });

       });      
      }
    
    componentDidMount(){
       this.getData();
    }     


    handleInputChange(e) {
         e.preventDefault();
         const {value} = e.target;
         console.log('valu',value)

         let counter=1
         let arreglo1=[]
         let arreglo2=[]
 
         console.log('this.state.data.',this.state.data)
         _.forEach(this.state.data.values, (n, key)=> {
             arreglo1.push(n)
             arreglo2.push(counter)
             counter++
           });        

           let state = {
            labels: arreglo2,
            datasets: [
              {
                label: 'BICE ESTADOS',
                fill: false,
                lineTension: 0.5,
                backgroundColor: 'rgba(175,192,192,1)',
                borderColor: 'rgba(255,2,255,1)',
                borderWidth: 2,
                data: arreglo1
              }
            ]
         }
         this.setState({
            grafica: state
         });

       }

       
    render(){
        let combo = this.state.combo;
        let optionItems = combo.map((combo) =>
                <option key={combo.name}>{combo.name}</option>
            );        

         return(
             <div>

                        <div className="col-sm-6">
                          <div className="card">
                            <div className="card-body">
                            <Line
                                data={this.state.grafica}
                                options={{
                                    title:{
                                    display:true,
                                    text:'Grafica mensual',
                                    fontSize:40
                                    },
                                    legend:{
                                    display:true,
                                    position:'right'
                                    }
                                }}
                                />
                             </div>
                          </div>
                        </div>


                        <div className="col-sm-8">
                          <div className="card">
                            <div className="card-body">
                            <select name="priority"
                                            className="form-control"
                                            value={this.state.priority}
                                            onChange={this.handleInputChange}>
                                            {optionItems}
                                    </select>                


                                {/* <select
                                    name="priority"
                                    className="form-control"
                                    value={this.state.priority}
                                    onChange={this.handleInputChange}
                                >
                                    <option>SELECCIONE AQUI PARA DESPLEGAR GRAFICA</option>
                                    <option>cobre</option>
                                    <option>dolar</option>
                                    <option>euro</option>
                                    <option>ipc</option>
                                    <option>ivp</option>
                                    <option>oro</option>
                                    <option>plata</option>
                                    <option>uf</option>
                                    <option>utm</option>
                                    <option>yen</option>      

                                </select> */}
                                
                                <p className="card-text"> </p>

                                <div className="card" >
                                   <div className="card">
                                    <button className="btn btn-primary">{this.state.data.key}</button>
                                    </div>
                                    <div className="card">
                                    <button className="btn btn-primary">{this.state.data.name}</button>
                                    </div>
                                    <div className="card">
                                    <button className="btn btn-primary">{this.state.data.unit}</button>
                                    </div>
                                </div>
                             </div>
                           </div>
                        </div>

                        <div className="card text-white bg-primary mb-12" >
                            <form action="/" method="get" >
                                <button className="btn btn-primary"type="submit">Volver</button>
                            </form>                
                        </div>

             </div>
        )
    }


}
export default BiceGraficas;
